document.querySelector(".toggle-sidebar").addEventListener("click",function(){document.querySelector(".sidebar").classList.toggle("no-sidebar"),document.querySelector(".content-area").classList.toggle("no-sidebar")}),document.querySelectorAll(".drop-down").forEach(function(e){e.addEventListener("click",function(){this.children[1].clientHeight>0?this.children[1].style.height=0:this.children[1].style.height=this.children[1].scrollHeight+"px",this.children[0].children[0].classList.toggle("rotate")})}),document.querySelector(".noti-toggle").addEventListener("click",function(){this.nextElementSibling.clientHeight?this.nextElementSibling.style.height=0:this.nextElementSibling.style.height=`${this.nextElementSibling.scrollHeight}px`});